drop schema if exists csvs cascade; 
create schema csvs; 
set search_path=csvs;


BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "quarterbacks"(
"id" TEXT, "name" TEXT, "team" TEXT, "position" TEXT,
 "completions" TEXT, "attempts" TEXT, "passing_yards" TEXT, "tds" TEXT,
 "interceptions" TEXT, "carries" TEXT, "rushing_yards" TEXT, "rushing_tds" TEXT,
 "outlook" TEXT);
CREATE TABLE IF NOT EXISTS "running_backs"(
"id" TEXT, "name" TEXT, "team" TEXT, "position" TEXT,
 "carries" TEXT, "rushing_yards" TEXT, "rushing_tds" TEXT, "receptions" TEXT,
 "receiving_yards" TEXT, "receiving_tds" TEXT, "outlook" TEXT);
CREATE TABLE IF NOT EXISTS "wide_receivers"(
"id" TEXT, "name" TEXT, "team" TEXT, "position" TEXT,
 "receptions" TEXT, "receiving_yards" TEXT, "receiving_tds" TEXT, "carries" TEXT,
 "rushing_yards" TEXT, "rushing_tds" TEXT, "outlook" TEXT);
CREATE TABLE IF NOT EXISTS "tight_ends"(
"id" TEXT, "name" TEXT, "team" TEXT, "position" TEXT,
 "receptions" TEXT, "receiving_yards" TEXT, "receiving_tds" TEXT, "carries" TEXT,
 "rushing_yards" TEXT, "rushing_tds" TEXT, "outlook" TEXT);
CREATE TABLE IF NOT EXISTS "defense"(
"id" TEXT, "name" TEXT, "sacks" TEXT, "interceptions" TEXT,
 "fumbles" TEXT, "tds" TEXT, "outlook" TEXT);
COMMIT;
